create schema news;

create table TEXT_SUMMARY
(
    id              int auto_increment
        primary key,
    title           varchar(255)                        null,
    content         blob                                null,
    keywords        varchar(255)                        null,
    summary         varchar(512)                        null,
    url             varchar(255)                        null,
    insertTimestamp timestamp default CURRENT_TIMESTAMP null,
    newsTimestamp   timestamp                           null,
    constraint TEXT_SUMMARY_url_uindex
        unique (url)
);

